init:
struct TreeNode {
    id: uid()
    data: Map()
    parent: null
    x: 0
    y: 0
    siblingIdx: 0
    level: 0
    children: List()


    map(callback) {
        mappedChildren = List()
        this.children.forEach((childNode) => {
            mappedChildren.add(childNode.map(callback))
        })
        callback(this, mappedChildren)
    }

    attachTo(parentNode) {
        this.parent = parentNode
        this.siblingIdx = parentNode.children.size
        parentNode.children.add(this)
    }

    attachChildAtIndex(childNode, idx) {
        childNode.parent = this
        this.children.insert(idx, childNode)
        this.children.forEach((node, nodeIdx) => {
            node.siblingIdx = nodeIdx
        })
    }

    encodeTree(nodeSeparator, paramSeparator) {
        encoded = this.id
        this.children.forEach((childNode) => {
            childEncoded = childNode.encodeTree(nodeSeparator, paramSeparator)
            encoded += nodeSeparator + childEncoded
        })
        encoded + nodeSeparator + 'N'
    }

    reindex(callback) {
        this.level = if (this.parent) { this.parent.level + 1 } else { 0 }
        if (this.children.size > 0) {
            this.children.forEach((childNode) => {
                childNode.reindex(callback)
            })
        }
        if (callback) {
            callback(this)
        }
    }
}

decodeTreeNode = (nodeEncoded, paramSeparator) => {
    nodeParts = splitString(nodeEncoded, paramSeparator)
    nodeId = nodeParts.shift()
    nodeData = Map()
    nodeParts.forEach((encodedParameter) => {
        paramParts = splitString(encodedParameter, '=')
        if (paramParts.size == 2) {
            nodeData.set(paramParts.get(0), paramParts.get(1))
        }
    })

    TreeNode(nodeId, nodeData)
}

decodeTree = (encodedText, nodeSeparator, paramSeparator) => {
    nodeList = splitString(encodedText, nodeSeparator)
    rootNode = decodeTreeNode(nodeList.shift(), paramSeparator)
    currentNode = rootNode
    while(nodeList.size > 0) {
        nodeEncoded = nodeList.shift()
        if (nodeEncoded == 'N') {
            parent = currentNode.parent
            if (!parent) {
                parent = rootNode
            }
            currentNode = parent
        } else {
            newNode = decodeTreeNode(nodeEncoded, paramSeparator)
            newNode.attachTo(currentNode)
            currentNode = newNode
        }
    }
    rootNode
}


struct Connector {
    id: ''
    srcId: ''
    dstId: ''
    p1: Vector(0, 0)
    p2: Vector(0, 0)
    x: 0
    y: 0
    w: 0
    h: 0

    updateArea() {
        this.x = min(this.p1.x, this.p2.x)
        this.y = min(this.p1.y, this.p2.y)
        this.w = abs(this.p1.x - this.p2.x)
        this.h = abs(this.p1.y - this.p2.y)
    }
}

reindexTree = () => {
    rootNode.reindex((node) => {
        fullWidth = 0
        if (node.children.size > 0) {
            node.children.forEach((childNode) => {
                fullWidth += childNode.data.get('fullWidth')
            })
            fullWidth = fullWidth + (node.children.size - 1) * hGap
        } else {
            fullWidth = width
        }
        node.data.set('fullWidth', fullWidth)
    })
}


items = List()
rootNode = decodeTree(nodes, ',', ':')
reindexTree()

connectors = List()

nodesById = Map()

visitTree = (node, parent) => {
    nodesById.set(node.id, node)

    if (!parent) {
        node.x = 0
        node.y = 0
    } else {
        node.y = node.level * (height + vGap)
    }
    items.add(node)

    axis = node.x + width/2
    childOffset = axis - node.data.get('fullWidth')/2
    node.children.forEach((childNode) => {
        childFullWidth = childNode.data.get('fullWidth')
        childNode.x = childOffset + (childFullWidth - width)/2
        childOffset = childNode.x + (width + childFullWidth)/2 + hGap
        visitTree(childNode, node)

        connector = Connector(`connector_${node.id}_${childNode.id}`, node.id, childNode.id)
        connector.p1 = Vector(node.x + width/2, node.y + height)
        connector.p2 = Vector(childNode.x + width/2, childNode.y)
        connector.updateArea()
        connectors.add(connector)
    })
}

visitTree(rootNode, 0, null)

createNewChildFor = (nodeId) => {
    node = nodesById.get(nodeId)
    if (node) {
        TreeNode(uid()).attachTo(node)
        reindexTree()
    }
    nodes = rootNode.encodeTree(',', ':')
}

createNewSiblingFor = (nodeId, idx) => {
    node = nodesById.get(nodeId)
    if (node && node.parent) {
        node.parent.attachChildAtIndex(TreeNode(uid()), idx)
        reindexTree()
    }
    nodes = rootNode.encodeTree(',', ':')
}

on('delete', (itemId, item) => {
    node = nodesById.get(itemId)
    if (node && node.parent) {
        node.parent.children.remove(node.siblingIdx)
        reindexTree()
    }
    nodes = rootNode.encodeTree(',', ':')
})
